(89, 'avalara_q3-2023_noAvaTax', 'marketing', 'en_US', 'Automatically calculate VAT in real time', 'Take the effort out of determining tax rates and sell confidently across borders with automated tax management from Avalara AvaTax— including built-in VAT calculation when you sell into or across the EU and UK. Save time and stay compliant when you let Avalara do the heavy lifting.', '{}', 'pending', 'woocommerce.com', '2023-09-07 06:16:19', NULL, 0, 'plain', '', 0, 0, 'info'),
(90, 'woo-activation-survey-blockers-22AUG23', 'info', 'en_US', 'How can we help you get that first sale?', 'Your feedback is vital. Please take a minute to share your experience of setting up your new store and whether anything is preventing you from making those first few sales. Together, we can make Woo even better!', '{}', 'pending', 'woocommerce.com', '2023-09-07 06:16:19', NULL, 0, 'plain', '', 0, 0, 'info'),
(91, 'tiktok_shopGTM_q3-2023_hasTikTok', 'marketing', 'en_US', 'You can now sell your products on TikTok Shop', 'TikTok is changing the way shoppers find and connect with brands. With TikTok Shop, you can sync your entire catalog from within Woo and sell directly to TikTok users through in-feed videos, livestreams, and your product showcase tab.', '{}', 'pending', 'woocommerce.com', '2023-09-07 06:16:19', NULL, 0, 'plain', '', 0, 0, 'info'),
(92, 'tiktok_shopGTM_q3-2023_noTikTok', 'marketing', 'en_US', 'You can now sell your products on TikTok Shop', 'TikTok is changing the way shoppers find and connect with brands. With TikTok Shop, you can sync your entire catalog from within Woo and sell directly to TikTok users through in-feed videos, livestreams, and your product showcase tab.', '{}', 'pending', 'woocommerce.com', '2023-09-08 12:08:38', NULL, 0, 'plain', '', 0, 0, 'info'),
(93, 'klaviyo_q3_2023', 'marketing', 'en_US', 'Build long lasting relationships with Klaviyo for WooCommerce', 'Increase customer engagement and boost conversions with powerful email and SMS automation from Klaviyo. Fully integrated with your Woo store, Klaviyo enables you to send personalized communications to your customers — ensuring that the right message is delivered at the right time for maximum impact.', '{}', 'pending', 'woocommerce.com', '2023-09-12 12:24:16', NULL, 0, 'plain', '', 0, 0, 'info');
